targets.expr:
(
    sum by (pod) (
        increase(
            kube_pod_container_status_restarts_total{cluster="$cluster", namespace="$namespace", container="bloom-compactor"}[10m]
        )
    )
    * on (pod) group_right
    max by (pod, reason) (
        kube_pod_container_status_last_terminated_reason{cluster="$cluster", namespace="$namespace", container="bloom-compactor"}
    )
) > 0

quantile(
    0.99,
    max by(persistentvolumeclaim) (
        kubelet_volume_stats_used_bytes{cluster=~"$cluster", namespace=~"$namespace"} 
        / 
        kubelet_volume_stats_capacity_bytes{cluster=~"$cluster", namespace=~"$namespace"}
    ) 
    and 
    count by(persistentvolumeclaim) (
        kube_persistentvolumeclaim_labels{cluster=~"$cluster", namespace=~"$namespace",label_name=~"bloom-compactor"}
    )
)

quantile(
    0.90,
    max by(persistentvolumeclaim) (
