quantile(
    0.99,
    sum by(instance, pod, device) (
        rate(node_disk_written_bytes_total[$__rate_interval])
    ) 
    + ignoring(pod) group_right() 
    (
        label_replace(
            count by(instance, pod, device) (
                container_fs_writes_bytes_total{cluster=~"$cluster", namespace=~"$namespace", container="bloom-compactor", device!~".*sda.*"}
            ), 
            "device", "$1", "device", "/dev/(.*)"
        ) * 0
    )
)

