targets.expr:
sum by(instance, device) (
  rate(node_disk_written_bytes_total[$__rate_interval])
)
+
ignoring(pod) group_right() (
  label_replace(
    count by (instance, pod, device) (
      container_fs_writes_bytes_total{cluster="$cluster", container="loki", namespace="$namespace", pod=~"((write)-([0-9]+|[a-z0-9]{10}-[a-z0-9]{5}))", device!~".*sda.*"}
    ),
    "device", "$1", "device", "/dev/(.*)"
  )
  * 0
)

sum by(instance, device) (
  rate(node_disk_read_bytes_total[$__rate_interval])
)
+
ignoring(pod) group_right() (
  label_replace(
    count by (instance, pod, device) (
      container_fs_writes_bytes_total{cluster="$cluster", container="loki", namespace="$namespace", pod=~"((write)-([0-9]+|[a-z0-9]{10}-[a-z0-9]{5}))", device!~".*sda.*"}
    ),
    "device", "$1", "device", "/dev/(.*)"
  )
  * 0
)

max by (persistentvolumeclaim) (
  kubelet_volume_stats_used_bytes{cluster="$cluster", namespace="$namespace", persistentvolumeclaim=~".*([a-z0-9]{10}-[a-z0-9]{5}))"|cluster="$cluster", container="loki", namespace="$namespace", pod=~"((write)-([0-9]+).*"}
  /
  kubelet_volume_stats_capacity_bytes{cluster="$cluster", namespace="$namespace", persistentvolumeclaim=~".*([a-z0-9]{10}-[a-z0-9]{5}))"|cluster="$cluster", container="loki", namespace="$namespace", pod=~"((write)-([0-9]+).*"}
)

sum by(instance, device) (
  rate(node_disk_written_bytes_total[$__rate_interval])
)
+
ignoring(pod) group_right() (
  label_replace(
    count by (instance, pod, device) (
      container_fs_writes_bytes_total{cluster="$cluster", container="loki", namespace="$namespace", pod=~"((backend)-([0-9]+|[a-z0-9]{10}-[a-z0-9]{5}))", device!~".*sda.*"}
    ),
    "device", "$1", "device", "/dev/(.*)"
  )
  * 0
)

sum by(instance, device) (
  rate(node_disk_read_bytes_total[$__rate_interval])
)
+
ignoring(pod) group_right() (
  label_replace(
    count by (instance, pod, device) (
      container_fs_writes_bytes_total{cluster="$cluster", container="loki", namespace="$namespace", pod=~"((backend)-([0-9]+|[a-z0-9]{10}-[a-z0-9]{5}))", device!~".*sda.*"}
    ),
    "device", "$1", "device", "/dev/(.*)"
  )
  * 0
)

max by (persistentvolumeclaim) (
  kubelet_volume_stats_used_bytes{cluster="$cluster", namespace="$namespace", persistentvolumeclaim=~".*([a-z0-9]{10}-[a-z0-9]{5}))"|cluster="$cluster", container="loki", namespace="$namespace", pod=~"((backend)-([0-9]+).*"}
  /
  kubelet_volume_stats_capacity_bytes{cluster="$cluster", namespace="$namespace", persistentvolumeclaim=~".*([a-z0-9]{10}-[a-z0-9]{5}))"|cluster="$cluster", container="loki", namespace="$namespace", pod=~"((backend)-([0-9]+).*"}
)
