targets.expr:
(
    sum by (pod) (
        increase(
            kube_pod_container_status_restarts_total{cluster="$cluster", namespace="$namespace", container="bloom-builder"}[10m]
        )
    )
    * on (pod) group_right
    max by (pod, reason) (
        kube_pod_container_status_last_terminated_reason{cluster="$cluster", namespace="$namespace", container="bloom-builder"}
    )
) > 0

(
    sum by (pod) (
        increase(
            kube_pod_container_status_restarts_total{cluster="$cluster", namespace="$namespace", container="bloom-planner"}[10m]
        )
    )
    * on (pod) group_right
    max by (pod, reason) (
        kube_pod_container_status_last_terminated_reason{cluster="$cluster", namespace="$namespace", container="bloom-planner"}
    )
) > 0
