targets.expr:
sum by(instance, device) (
  rate(node_disk_written_bytes_total[$__rate_interval])
)
+
ignoring(pod) group_right() (
  label_replace(
    count by (instance, pod, device) (
      container_fs_writes_bytes_total{cluster="$cluster", container=~"(querier)", namespace="$namespace", device!~".*sda.*"}
    ),
    "device", "$1", "device", "/dev/(.*)"
  )
  * 0
)

